package tutorial;

public class Counting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int y = 0;
		int z = 0;
		
		for (int x = 0; x <= 10; x ++ ) {
			System.out.println(x);
		}
		while (y <= 10) {
			System.out.println(y);
			y ++;
		}
		do {
			System.out.println(z);
			z ++;
		}
		while (z <= 10);
	}

}
